    uint256 constant IC2y = 11177231060708742743358324988553470547814400821948073313753773706989847295644;

    uint256 constant IC3x = 3408663513345353973053132612265015137557556841684826865694471459344719427303;
    uint256 constant IC3y = 10272416661617104036394590923859909187480648619637684251560066426368659564981;

    // Memory data
    uint16 constant pVk = 0;
    uint16 constant pPairing = 128;

